(1, '62148f6026541', 'Advanced WooCommerce Support', 'We have detected you are running WooCommerce. Upgrade to AIOSEO Pro to unlock our advanced eCommerce SEO features, including SEO for Product Categories and more.', 'info', '[\"all\"]', NULL, 'woo-upsell', '2022-02-22 07:23:12', NULL, 'Upgrade to Pro', 'https://aioseo.com/lite-upgrade/?utm_source=WordPress&utm_campaign=liteplugin&utm_medium=woo-notification-upsell', NULL, NULL, 0, 0, '2022-02-22 07:23:12', '2022-02-22 07:23:12'),
(2, '621e528dde369', '📢 Announcing 4.1.8: Improved Site Redirects and Modified Date for Posts 📢', 'Redirects have never been easier! Our 4.1.8 update comes with a powerful feature that enables you to implement a redirect from right within a post.\r\n<br><br>\r\nWe’ve also added a \"limit modified date\" feature that allows you to make minor edits to posts/pages and keep the date the same.\r\n<br><br>\r\nUpdate to AIOSEO 4.1.8 today and enjoy these 2 powerful features and more.', 'success', '[\"4-x\",\"lite\"]', 241, NULL, '2022-04-08 19:59:50', NULL, 'Learn More', 'https://aioseo.com/announcing-improved-site-redirects-and-modified-date-for-posts/?utm_source=WordPress&utm_campaign=introducing-on-page-redirects-and-limit-modified-date-v4-lite&utm_medium=plugin-notification&utm_content=Learn More', 'Get AIOSEO Pro', 'https://aioseo.com/lite-upgrade/?utm_source=WordPress&utm_campaign=introducing-on-page-redirects-and-limit-modified-date-v4-lite&utm_medium=plugin-notification&utm_content=Get AIOSEO Pro', 0, 0, '2022-03-01 17:06:21', '2022-04-25 17:04:23'),
(3, '621e528ddf414', '🎉 Introducing IndexNow and Page Builder Integrations', 'Check out the fantastic new features in AIOSEO 4.1.7! First, we have IndexNow, a feature that gets your new or updated content indexed faster on Bing and Yandex (with more search engines to follow).\r\n<br><br>\r\nWe’ve also integrated AIOSEO with 3 major page builders, namely Elementor, Divi and SeedProd. Now you can work on your SEO right from within your page builder.\r\n<br><br>\r\nGet AIOSEO Pro today and up your SEO game without breaking a sweat!', 'success', '[\"4-x\",\"lite\"]', 223, NULL, '2022-03-01 16:19:06', NULL, 'Learn More', 'https://aioseo.com/introducing-indexnow-and-page-builder-integrations-in-aioseo-4-1-7/?utm_source=WordPress&utm_campaign=index-now-v4-lite&utm_medium=plugin-notification&utm_content=Learn More', 'Get AIOSEO Pro', 'https://aioseo.com/lite-upgrade/?utm_source=WordPress&utm_campaign=index-now-v4-lite&utm_medium=plugin-notification&utm_content=Get AIOSEO Pro', 0, 0, '2022-03-01 17:06:21', '2022-03-25 18:55:05'),
(4, '624425ecb2165', 'Microsoft Clarity Integration and a New REST API Addon 🎉🎉', 'Now you can see how your website visitors interact with your web pages with heatmaps and session recordings, thanks to Microsoft Clarity.\r\n<br><br>\r\nWe’ve also added a REST API addon to make SEO easier for your headless website.\r\n<br><br>\r\nUpdate to AIOSEO 4.1.9 today and enjoy these 2 powerful features on your website!', 'success', '[\"4-x\",\"lite\"]', 263, NULL, '2022-05-03 15:26:42', NULL, 'Learn More', 'https://aioseo.com/introducing-the-new-microsoft-clarity-integration-and-rest-api-addon/?utm_source=WordPress&utm_campaign=microsoft-clarity-rest-api-v4-lite&utm_medium=plugin-notification&utm_content=Learn More', 'Get AIOSEO Pro', 'https://aioseo.com/lite-upgrade/?utm_source=WordPress&utm_campaign=microsoft-clarity-rest-api-v4-lite&utm_medium=plugin-notification&utm_content=Get AIOSEO Pro', 0, 0, '2022-03-30 09:42:04', '2022-05-31 08:36:02'),
